    isBottomDragging: false,
    topY: "10%",
    topX: "50%",
    bottomX: "50%",
    bottomY: "90%"
}; 

export default class Gallery extends Component {




    constructor(){
        super();
        this.state={
            isLoaded: false,
            imageIsOpen :false,
